<template>    <div class="wrap">        <div>            <img v-show="imageIndex === index" :class="imageIndex === index ? 'image image-active' : 'image'"                 v-for="(item, index) in imageList" :src="item" alt="">        </div>        <div class="option">            <div class="pre" @click="imageIndex === 0 ? switchImg(imageList.length - 1) : switchImg(imageIndex - 1)">                &lt;            </div>            <div class="next" @click="imageIndex === imageList.length - 1 ? switchImg(0) : switchImg(imageIndex + 1)">                &gt;            </div>        </div>        <div class="dot" ref="dot">            <div :class="index === imageIndex ? 'active' : ''" @click="switchImg(index)"                 v-for="(item, index) in imageList" :key="index"></div>        </div>    </div></template><script>import logo from '../assets/logo.png'import dir from '../assets/dir.png'import md from '../assets/md.png'import notImage from '../assets/notImage.jpg'import txt from '../assets/txt.png'import zip from '../assets/zip.png'import {onMounted, reactive, ref} from 'vue'export default {    name: 'BroadcastMap',    setup() {        let imageIndex = ref(0)        let imageList = reactive([            logo,            dir,            md,            notImage,            txt,            zip,        ])        let time = null        function startTime() {            time = setInterval(() => {                imageIndex.value += 1                imageIndex.value = imageIndex.value === imageList.length ? 0 : imageIndex.value            }, 2000)        }        onMounted(() => {            startTime()        })        function switchImg(index) {            imageIndex.value = index            clearTimeout(time)            startTime()        }        return {            logo,            imageList,            imageIndex,            switchImg,        }    },}</script><style scoped>.wrap {    position: relative;    min-width: 500px;    min-height: 363px;    overflow: hidden;    max-width: 628px;    max-height: 512px;    margin-right: 10px;}.image {    width: 100%;    opacity: 0;}.option {    display: flex;    font-size: 20px;    justify-content: space-between;    position: absolute;    bottom: 8%;    right: 10px;}.option div {    width: 30px;    height: 30px;    background-color: rgb(143 139 139 / 50%);;    text-align: center;    color: #fdfeff;    cursor: pointer;    border-radius: 8px;    margin: 6px;}.dot {    position: absolute;    bottom: 20px;    left: 10px;    display: flex;    width: 100%;}.dot div {    width: 10px;    height: 10px;    background-color: #a6a7a8;    margin: 0 5px;    border-radius: 50%;    cursor: pointer;}.active {    background-color: #2a2a2a !important;}.image-active {    animation: scale .3s linear;    opacity: 1;}@keyframes scale {    25% {        opacity: .25;    }    50% {        opacity: .5;    }    75% {        opacity: .75;    }    100% {        opacity: 1;    }}</style>