<template>    <div class="title">核心数据概览</div>    <div class="info">        <div class="data-card">            <div :class="tabIndex === 0 ? 'data-detail card-active' : 'data-detail'" @click="tabIndex = 0">                <div class="name">播放量</div>                <div class="value">{{ playNum }}</div>            </div>        </div>        <div class="data-card">            <div :class="tabIndex === 1 ? 'data-detail card-active' : 'data-detail'" @click="tabIndex = 1">                <div class="name">净增</div>                <div class="value">{{ subscribeNum }}</div>            </div>        </div>        <div class="data-card">            <div :class="tabIndex === 2 ? 'data-detail card-active' : 'data-detail'" @click="tabIndex = 2">                <div class="name">点赞</div>                <div class="value">{{ starNum }}</div>            </div>        </div>        <div class="data-card">            <div :class="tabIndex === 3 ? 'data-detail card-active' : 'data-detail'" @click="tabIndex = 3">                <div class="name">评论</div>                <div class="value">{{ commentNum }}</div>            </div>        </div>        <div class="data-card">            <div :class="tabIndex === 4 ? 'data-detail card-active' : 'data-detail'" @click="tabIndex = 4">                <div class="name">弹幕</div>                <div class="value">{{ barrageNum }}</div>            </div>        </div>    </div>    <div>        <LastSevenDaysPlayNum v-if="tabIndex === 0"/>        <LastSevenDaysFanNum v-if="tabIndex === 1"/>        <LastSevenDaysStarNum v-if="tabIndex === 2"/>        <LastSevenDaysCommentNum v-if="tabIndex === 3"/>        <LastSevenDaysBarrageNum v-if="tabIndex === 4"/>    </div></template><script>import {onMounted, ref} from 'vue'import request from '@/utils/request'import {    GetAllBarrageData,    GetAllCommentData,    GetAllPlayData,    GetAllStarData,    GetYesterdaySubscribeData,    VIDEO_TYPE,} from '@/utils/Constant'import LastSevenDaysFanNum from '@/components/LastSevenDaysFanNum'import LastSevenDaysPlayNum from '@/components/LastSevenDaysPlayNum'import LastSevenDaysStarNum from '@/components/LastSevenDaysStarNum'import LastSevenDaysCommentNum from '@/components/LastSevenDaysCommentNum'import LastSevenDaysBarrageNum from '@/components/LastSevenDaysBarrageNum'export default {    name: 'CoreData',    components: {        LastSevenDaysBarrageNum,        LastSevenDaysCommentNum, LastSevenDaysStarNum, LastSevenDaysPlayNum, LastSevenDaysFanNum},    setup() {        let tabIndex = ref(0)        let playNum = ref(0)        let subscribeNum = ref(0)        let starNum = ref(0)        let commentNum = ref(0)        let barrageNum = ref(0)        function init() {            request.get(`${GetAllPlayData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    playNum.value = res.data.data.playNum                }            })            request.get(GetYesterdaySubscribeData).then(res => {                if (res.data.code === 0) {                    subscribeNum.value = res.data.data.subscribeNum                }            })            request.get(`${GetAllStarData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    starNum.value = res.data.data.starNum                }            })            request.get(`${GetAllCommentData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    commentNum.value = res.data.data.commentNum                }            })            request.get(`${GetAllBarrageData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    barrageNum.value = res.data.data.barrageNum                }            })        }        onMounted(() => {            init()        })        return {            tabIndex,            playNum,            subscribeNum,            starNum,            commentNum,            barrageNum,        }    }}</script><style scoped>.title{    margin-right: 4px;    color: #18191c;    font-weight: 600;    font-size: 18px;    line-height: 25px;    margin-left: 8px;}.info {    display: flex;    flex-wrap: wrap;    margin-top: 20px;}.data-card{    display: inline-block;    width: 195px;    height: 88px;    padding: 20px 6px;    vertical-align: middle;    position: relative;}.data-detail{    display: flex;    flex-direction: column;    justify-content: space-between;    width: 100%;    height: 100%;    box-sizing: border-box;    padding: 16px 16px 20px 16px;    color: #61666d;    cursor: pointer;    background-color: #fff;    border-radius: 16px;}.card-active{    background: #ff4684;    color: #fff;}.name{    font-size: 14px;    opacity: 0.8;}.value{    font-weight: 800;    font-size: 22px;    line-height: 22px;    box-sizing: border-box;    letter-spacing: 1px;}</style>