<template>    <div class="send">        <div class="input" @input="content=$event.target.innerText" contenteditable="true"></div>        <div class="par">            <div v-show="content !== ''" @click="submitDynamic()" class="button">发布</div>            <div v-show="content === ''" class="button disabled">发布</div>        </div>    </div></template><script>import {ref} from 'vue'import request from '@/utils/request'import {SubmitDynamic} from '@/utils/Constant'export default {    name: 'DynamicSend',    setup() {        let content = ref('')        let type = ref(0)        function submitDynamic() {            request.post(SubmitDynamic, {                title: content.value,                type: type.value,            }).then(res => {                if (res.data.code === 0) {                    content.value = ''                }            })        }        return {            content,            submitDynamic,        }    },}</script><style scoped>.send {    width: 632px;    background-color: white;    margin-bottom: 10px;    border-radius: 4px;    display: flex;    flex-direction: column;}.input {    outline: none;    margin: 15px 10px 10px 10px;}.par {    display: flex;    flex-direction: column;    justify-content: flex-end;    align-items: flex-end;}.button {    width: 70px;    display: flex;    justify-content: center;    align-items: center;    background-color: #00a1d6;    color: white;    border-radius: 4px;    height: 32px;    margin-bottom: 10px;    margin-right: 10px;    font-size: 14px;    cursor: pointer;}.button:hover {    background-color: #00abe0;}.disabled {    opacity: .5;    cursor: not-allowed;}</style>