<template>    <div class="article" @click="to(`/read/${item.id}`)">        <img class="cover" :src="item.coverSrc !== null ? item.coverSrc : logo" alt="">        <div class="article-detail">            <div class="article-title">{{ item.title }}</div>            <div class="article-desc">{{ item.content }}</div>        </div>    </div></template><script>import logo from '@/assets/logo.png'import {useRouter} from 'vue-router'export default {    name: 'DynamicTextItem',    props: {        item: {            type: Object,        },    },    setup() {        let router = useRouter()        function to(path) {            window.open(router.resolve(path).href, '_blank')        }        return {            logo,            to,        }    },}</script><style scoped>.article {    width: 518px;    border-radius: 4px;    border: #e5e9ef solid 1px;    cursor: pointer;}.cover {    width: 100%;    height: 120px;    border-top-left-radius: 4px;    border-top-right-radius: 4px;}.article-detail {    padding: 10px 20px 14px;}.article-title {    color: #212121;    font-size: 16px;    height: 22px;    line-height: 22px;    margin-bottom: 6px;    overflow: hidden;    text-overflow: ellipsis;    transition: color .2s;    white-space: nowrap;}.article-desc {    color: #666;    font-size: 12px;    line-height: 19px;    display: -webkit-box;    overflow: hidden;    text-overflow: ellipsis;    word-break: break-all;    -webkit-box-orient: vertical;    -webkit-line-clamp: 2;}</style>