<template>    <div class="video-item" @click="toVideo(`/video/${item.id}`)">        <img class="video-cover" :src="item.coverSrc === null || item.coverSrc === '' ? logo : item.coverSrc" alt="">        <div class="video-detail">            <span class="video-title">{{ item.title }}</span>            <span class="video-introduce">{{ item.introduce }}</span>            <div class="video-num">                <div>                    <svg id="图层_1" xmlns="http://www.w3.org/2000/svg" x="0" y="0" viewBox="0 0 40 40"                         xml:space="preserve">    <path class="st0"          d="M32.9 2.9H7.1C3.2 2.8 0 6 0 9.9V30c0 3.9 3.1 7.1 7 7.1h25.8c3.9 0 7.1-3.1 7.1-7V10c0-3.9-3.1-7.1-7-7.2.1 0 0 0 0 .1zM37.2 30c0 2.3-1.8 4.2-4.1 4.3h-26c-2.3 0-4.2-1.8-4.3-4.1V10c0-2.3 1.8-4.2 4.2-4.3h25.9c2.3 0 4.2 1.8 4.3 4.1V30z"          fill="#999"/>                        <path class="st0"                              d="M27.7 18.7l-11.4-5c-.7-.3-1.5 0-1.9.7-.1.2-.1.4-.1.6v10c0 .8.6 1.4 1.4 1.4.2 0 .4 0 .6-.1l11.4-5c.7-.3 1-1.1.7-1.8-.1-.4-.4-.6-.7-.8z"                              fill="#999"/></svg>                    <span>{{ playNum }}</span>                </div>                <div>                    <svg data-name="图层 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40">                        <path                            d="M32.86 2.84A7.08 7.08 0 0140 10v20a7.08 7.08 0 01-7.14 7.15H7.14A7.08 7.08 0 010 30V10a7.08 7.08 0 017.14-7.16zM37.14 30V10a4.21 4.21 0 00-4.28-4.3H7.14A4.21 4.21 0 002.86 10v20a4.21 4.21 0 004.28 4.3h25.72a4.21 4.21 0 004.28-4.3zM7.86 17.14v-2.86h2.86v2.86zm2.86 8.58v-2.86h2.86v2.86zm5.71 0v-2.86h15.71v2.86zm-2.86-8.58v-2.86h17.14v2.86z"                            fill="#999"/>                    </svg>                    <span>{{ barrageNum }}</span>                </div>            </div>        </div>    </div></template><script>import logo from '@/assets/logo.png'import {useRouter} from 'vue-router'import {onMounted, ref} from 'vue'import request from '@/utils/request'import {GetBarrageNumByDynamicId, GetPlayNumByDynamicId} from '@/utils/Constant'export default {    name: 'DynamicVideoItem',    props: {        item: {            type: Object,        },    },    setup(props) {        let router = useRouter()        let playNum = ref(0)        let barrageNum = ref(0)        function toVideo(path) {            window.open(router.resolve(path).href, '_blank')        }        function init() {            request.get(`${GetPlayNumByDynamicId}/${props.item.id}`).then(res => {                if (res.data.code === 0) {                    playNum.value = res.data.data.playNum                }            })            request.get(`${GetBarrageNumByDynamicId}/${props.item.id}`).then(res => {                if (res.data.code === 0) {                    barrageNum.value = res.data.data.barrageNum                }            })        }        onMounted(() => {            init()        })        return {            logo,            toVideo,            playNum,            barrageNum,        }    },}</script><style scoped>.video-title {    overflow: hidden;    text-overflow: ellipsis;    display: -webkit-box;    -webkit-line-clamp: 2;    line-clamp: 2;    -webkit-box-orient: vertical;    white-space: normal;    font-size: 14px;    transition: all .3s;}.video-item {    width: 510px;    height: 129px;    border-radius: 4px;    cursor: pointer;    display: flex;    border: #e5e9ef solid 1px;}.video-item:hover .video-title {    color: #23aedb;}.video-cover {    width: 203px;    height: 127px;    border-top-left-radius: 4px;    border-bottom-left-radius: 4px;}.video-detail {    padding: 12px;    display: flex;    flex-direction: column;    width: 283px;    position: relative;    border-top-right-radius: 4px;    border-bottom-right-radius: 4px;}.video-introduce {    font-size: 12px;    color: #666666;    margin: 10px 0;    overflow: hidden;    text-overflow: ellipsis;    display: -webkit-box;    -webkit-line-clamp: 2;    line-clamp: 2;    -webkit-box-orient: vertical;    white-space: normal;}svg {    width: 14px;}.video-num {    display: flex;    align-items: center;    font-size: 12px;    line-height: 12px;    position: absolute;    bottom: 12px;    color: #999999;}.video-num div {    display: flex;    justify-content: center;    align-items: center;    margin-right: 20px;}.video-num span {    margin-left: 4px;}</style>