<template>    <div class="item">        <div class="left">{{ item.playTime.replace('T', ' ') }}</div>        <div class="right">            <div class="img">                <img class="cover" :src="item.dynamic.coverSrc === null ? logo : item.dynamic.coverSrc" alt="">            </div>            <div class="detail">                <div class="video-title">{{ item.dynamic.title }}</div>                <div class="option"><span>收藏</span> | <span @click="deleteById(item.play.id)">删除记录</span></div>                <div class="author">                    <img class="avatar" @click="to(`/space/${item.user.id}`)"                         :src="item.user.avatar === '' ? logo : item.user.avatar" alt="">                    <span @click="to(`/space/${item.user.id}`)">{{ item.user.nickname }}</span>                </div>            </div>        </div>    </div></template><script>import logo from '@/assets/logo.png'import {useRouter} from 'vue-router'import request from '@/utils/request'import {DeletePlayById} from '@/utils/Constant'export default {    name: 'HistoryItem',    props: {        item: {            type: Object,        },    },    emits: ['update'],    setup(props, ctx) {        let router = useRouter()        function deleteById(id) {            request.get(`${DeletePlayById}/${id}`).then(res => {                if (res.data.code === 0) {                    ctx.emit('update')                }            })        }        function to(path) {            window.open(router.resolve(path).href, '_blank')        }        return {            logo,            deleteById,            to,        }    },}</script><style scoped>.cover {    border-radius: 4px;    width: 160px;    height: 100px;}.item {    display: flex;    justify-content: flex-start;    align-items: center;    font-size: 14px;    margin-bottom: 10px;}.right {    display: flex;}.left {    color: #999999;}.img {    margin: 0 30px;}.detail {    display: flex;    flex-direction: column;    justify-content: space-between;    width: 100%;    padding: 6px 0;    border-bottom: #e5e9ef solid 1px;}.video-title {    font-weight: bold;    overflow: hidden;    text-overflow: ellipsis;    width: 500px;}.option {    display: flex;    justify-content: flex-end;    color: #999999;    font-size: 12px;}.author {    display: flex;    justify-content: flex-end;    align-items: center;    color: #999999;}.avatar {    width: 30px;    height: 30px;    border-radius: 50%;    margin-right: 10px;    cursor: pointer;}.option span {    cursor: pointer;}.author span {    cursor: pointer;}</style>