<template>    <div class="chart">        <div id="chart"></div>    </div></template><script>import * as echarts from 'echarts'import {onMounted} from 'vue'import request from '@/utils/request'import {GetLastSevenDaysPlayData, VIDEO_TYPE} from '@/utils/Constant'export default {    name: 'LastSevenDaysPlayNum',    setup() {        let myChart = null        onMounted(() => {            myChart = echarts.init(document.getElementById('chart'))            init()        })        function init() {            request.get(`${GetLastSevenDaysPlayData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    myChart.setOption({                        title: {                            show: true,                            text: '近7天播放量',                        },                        xAxis: {                            type: 'category',                            data: Object.keys(res.data.data),                            axisTick: {                                show: false //不显示坐标轴刻度线                            },                            axisLine: {                                show: false, //不显示坐标轴线                            },                        },                        yAxis: {                            type: 'value',                            axisTick: {                                show: false //不显示坐标轴刻度线                            },                            axisLine: {                                show: false, //不显示坐标轴线                            },                            splitLine: {     //网格线                                "show": false                            }                        },                        series: [                            {                                data: Object.values(res.data.data),                                type: 'line',                            },                        ],                        tooltip: { // 鼠标悬浮提示框显示 X和Y 轴数据                            show: true,                            trigger: 'axis',                            showDelay: 0,//显示延时，添加显示延时可以避免频繁切换                            hideDelay: 50,//隐藏延时                            transitionDuration: 0,//动画变换时长                            borderRadius: 8,//边框圆角                            padding: 10,    // [5, 10, 15, 20] 内边距                            // axisPointer: { // 坐标轴虚线                            //     type: 'cross',                            //     label: {                            //         backgroundColor: '#6a7985'                            //     }                            // },                        },                    })                }                window.onresize = function() {//自适应大小                    myChart.resize()                }            })        }    }}</script><style scoped>#chart {    height: 268px;    padding: 24px 32px;    margin: 0 auto;}.chart {    background-color: white;    width: 1040px;    height: 332px;    border-radius: 20px;}</style>