<template>    <div class="message">        <div :class="`content ${type} hidden`" ref="message">{{msg}}</div>    </div></template><script>import {getCurrentInstance, onMounted} from 'vue'export default {    name: 'Message',    props: {        msg: {            type: String,        },        type: {            type: String        }    },    setup(props){        let message = null        onMounted(() => {            message = getCurrentInstance().refs.message            message.className = `content ${props.type}`            setTimeout(() => {                message.className = `content ${props.type} hidden`            }, 1500)        })    }}</script><style scoped>.message{    position: fixed;    z-index: 999;    top: 50px;    display: flex;    justify-content: center;    width: 100vw;}.content{    padding: 4px 10px;    border-radius: 4px;    background-color: #e8e8e8;    display: flex;    transition: all .3s;    min-width: 200px;    justify-content: center;}.hidden{    display: none;}.success{    background-color: #4bef8f;}.error{    background-color: #ff7575;}</style>