<template>    <div class="message">        <div class="left">            <img class="avatar" @click="toUser(item.user.id)" :src="item.user.avatar === '' ? logo : item.user.avatar"                 alt="">        </div>        <div class="middle">            <div class="detail"><span class="nickname" @click="toUser(item.user.id)">{{ item.user.nickname }}</span>                回复了我的评论            </div>            <div>回复 <span class="user" @click="toUser(item.replyUser.id)">@{{ item.replyUser.nickname }}</span>                : <span>{{ item.comment.content }}</span>            </div>            <div class="replyComment" v-if="item.replyCommentVO">                <span>{{ item.replyCommentVO.user.nickname }} : 回复 @{{                        item.replyCommentVO.replyUser.nickname                    }} : {{ item.replyCommentVO.comment.content }}</span>            </div>            <div class="option">                {{ item.comment.createTime.replace('T', ' ') }}            </div>        </div>        <div class="right"></div>    </div></template><script>import logo from '@/assets/logo.png'import {useRouter} from 'vue-router'export default {    name: 'MessageItem',    props: {        item: {            type: Object,        },    },    setup() {        let router = useRouter()        function toUser(id) {            window.open(router.resolve(`/space/${id}`).href, '_blank')        }        return {            logo,            toUser,        }    },}</script><style scoped>.message {    width: calc(100% - 20px);    display: flex;    margin: 10px 0;    align-content: flex-start;    justify-content: flex-start;    padding: 0 10px;}.avatar {    width: 46px;    height: 46px;    cursor: pointer;    border-radius: 50%;}.left {    width: 60px;}.middle {    display: flex;    flex-direction: column;    flex: 1;    max-width: calc(100% - 60px);    -webkit-line-clamp: 4;    line-clamp: 4;    word-break: break-word;    font-size: 14px;    border-bottom: #e5e9ef solid 1px;}.nickname {    font-weight: bold;    cursor: pointer;}.detail {    margin-bottom: 6px;}.user {    cursor: pointer;    color: #23aedb;}.user:hover {    color: #0ebde2;}/*.right {*//*    width: 60px;*//*}*/.replyComment {    color: #999999;    font-size: 12px;    margin: 4px 0;}.replyComment:before {    content: '|';    width: 4px;    color: #e7e7e7;}.option {    font-size: 12px;    color: #999999;    margin-top: 6px;    margin-bottom: 30px;}</style>