<template>    <div class="item">        <div class="time">{{ item.time.replace('T', ' ') }}</div>        <div class="message">            <div class="content">                <div v-if="item.type === SEND_TEXT_TYPE">{{ item.content }}</div>                <img class="image" v-if="item.type === SEND_IMAGE_TYPE" :src="BASEURL + item.content" alt="">            </div>            <img class="avatar" :src="user.avatar === '' ? logo : user.avatar" alt="">        </div>    </div></template><script>import logo from '@/assets/logo.png'import {SEND_IMAGE_TYPE, SEND_TEXT_TYPE, BASEURL} from '@/utils/Constant'export default {    name: 'MySendMessage',    props: {        item: {            type: Object        },        user: {            type: Object        }    },    setup() {        return {            logo,            SEND_IMAGE_TYPE,            SEND_TEXT_TYPE,            BASEURL        }    },}</script><style scoped>.item {    width: 100%;    display: flex;    flex-direction: column;}.time {    width: 100%;    display: flex;    justify-content: center;    color: #6d757a;    font-size: 12px;}.avatar {    border-radius: 50%;    width: 30px;    height: 30px;}.message {    display: flex;    justify-content: flex-end;    padding: 10px;}.content {    margin-top: 4px;    background-color: #80b9f2;    margin-right: 5px;    border-top-left-radius: 10px;    border-bottom-left-radius: 10px;    border-bottom-right-radius: 10px;    padding: 10px;    max-width: calc(100% - 30px - 20px - 20px);    word-wrap: break-word;    word-break: break-word;    color: white;}.image{    /*width: 200px;*/    width: 100%;}</style>