<template>    <div v-if="total >= 1" class="pagination">        <div class="option" v-show="pageNum > 1" @click="updatePageNum(pageNum - 1)">上一页</div>        <div class="page">            <div class="pageNum" @click="updatePageNum(1)" v-show="pageNum > 3">1</div>            <div class="pageNum" v-show="pageNum > 3">...</div>            <div class="pageNum" @click="updatePageNum(pageNum - 2)" v-show="pageNum > 2">{{ pageNum - 2 }}</div>            <div class="pageNum" @click="updatePageNum(pageNum - 1)" v-show="pageNum > 1">{{ pageNum - 1 }}</div>            <div class="pageNum active">{{ pageNum }}</div>            <div class="pageNum" @click="updatePageNum(pageNum + 1)" v-show="total - pageNum > 1">{{ pageNum + 1 }}            </div>            <div class="pageNum" @click="updatePageNum(pageNum + 2)" v-show="total - pageNum > 2">{{                    pageNum + 2                }}            </div>            <div class="pageNum" v-show="total - pageNum > 3">...</div>            <div class="pageNum" @click="updatePageNum(total)" v-show="total > pageNum">{{ total }}</div>        </div>        <div class="option" v-show="pageNum < total" @click="updatePageNum(pageNum + 1)">下一页</div>        <div class="step">            共 {{ total }} 页， 跳至 <input v-model.number="page" @keyup.enter="updatePageNum(page)" type="text"> 页        </div>    </div></template><script>import {ref} from 'vue'export default {    name: 'Pagination',    props: {        total: {            type: Number,        },        pageNum: {            type: Number,        },    },    emits: ['update'],    setup(props, ctx) {        let page = ref()        function updatePageNum(page) {            if (page > props.total) {                page = props.total            }            if (page < 1) {                page = 1            }            ctx.emit('update', page)        }        return {            updatePageNum,            page,        }    },}</script><style scoped>.pagination {    display: flex;    justify-content: center;    align-items: center;    width: 100%;    margin: 10px 0;}.option {    display: flex;    justify-content: center;    padding: 0 15px;    height: 38px;    align-items: center;    border: 1px solid #d7dde4;    border-radius: 4px;    margin: 0 4px;    cursor: pointer;    transition: all .3s;}.option:hover {    color: #00a1d6;    border-color: #00a1d6;}.page {    display: flex;    justify-content: center;}.pageNum {    display: flex;    height: 38px;    padding: 0 15px;    justify-content: center;    align-items: center;    margin: 0 4px;    cursor: pointer;    border: 1px solid #d7dde4;    border-radius: 4px;    transition: all .3s;}.pageNum:hover {    color: #00a1d6;    border-color: #00a1d6;}.active {    background-color: #00a1d6;    border-color: #00a1d6;    color: white !important;}.step {    font-size: 12px;    height: 38px;    display: flex;    align-items: center;    color: #99a2aa;    margin-left: 20px;}input {    border-radius: 4px;    margin: 0 8px;    width: 50px;    line-height: 28px;    height: 28px;    padding: 0 10px;    transition: all .3s ease;    vertical-align: top;    border: 1px solid #ccd0d7;    outline: none;}</style>