<template>    <div class="star">        <div class="left">            <img class="avatar" @click="toUser(item.user.id)" :src="item.user.avatar === '' ? logo : item.user.avatar"                 alt="">        </div>        <div class="middle">            <div class="detail"><span class="nickname" @click="toUser(item.user.id)">{{ item.user.nickname }}</span>                赞了我的{{ item.comment === null ? '视频' : '评论' }}            </div>            <div class="option">                {{ item.star.createTime.replace('T', ' ') }}            </div>        </div>        <div class="right">            {{ item.comment === null ? item.dynamic.content : item.comment.content }}        </div>    </div></template><script>import {useRouter} from 'vue-router'import logo from '@/assets/logo.png'export default {    name: 'StarItem',    props: {        item: {            type: Object,        },    },    setup() {        let router = useRouter()        function toUser(id) {            window.open(router.resolve(`/space/${id}`).href, '_blank')        }        return {            logo,            toUser,        }    },}</script><style scoped>.star {    width: 100%;    display: flex;    margin: 10px 0;    align-items: center;    justify-content: center;    border-bottom: #e5e9ef solid 1px;}.avatar {    width: 46px;    height: 46px;    cursor: pointer;    border-radius: 50%;}.left {    width: 60px;}.middle {    display: flex;    flex-direction: column;    flex: 1;    max-width: calc(100% - 60px - 60px);    -webkit-line-clamp: 4;    line-clamp: 4;    word-break: break-word;    font-size: 14px;}.detail {    margin-bottom: 6px;}.nickname {    font-weight: bold;    cursor: pointer;}.right {    width: 60px;    line-clamp: 4;    text-overflow: ellipsis;    font-size: 14px;    -webkit-box-orient: vertical;    -webkit-line-clamp: 4;    word-break: break-all;    display: -webkit-box;    overflow: hidden;    color: #6d757a;}.option {    font-size: 12px;    color: #999999;    margin-top: 6px;    margin-bottom: 10px;}</style>