<template>    <div class="item" @click="to(`/video/${item.dynamic.id}`)">        <div class="cover">            <img class="img" :src="item.dynamic.coverSrc === null ? logo : item.dynamic.coverSrc" alt="">        </div>        <div class="mask">            <div class="info">                <span>播放：{{ item.playNum }}</span>                <span>收藏：{{ item.collectNum }}</span>                <span>UP主：{{ item.user.nickname }}</span>                <span>投稿：{{ item.dynamic.createTime.split('T')[0] }}</span>            </div>        </div>        <span class="name">{{ item.dynamic.title }}</span>        <span class="time" v-if="item.collectTime !== null">收藏于： {{ item.collectTime.split('T')[0] }}</span>        <span class="time" v-if="item.collectTime === null">投稿于： {{ item.dynamic.createTime.split('T')[0] }}</span>    </div></template><script>import logo from '@/assets/logo.png'import {useRouter} from 'vue-router'export default {    name: 'VideoItem',    props: {        item: {            type: Object,        },    },    setup() {        let router = useRouter()        function to(path) {            window.open(router.resolve(path).href, '_blank')        }        return {            logo,            to        }    },}</script><style scoped>@media screen and (max-width: 1402px) {    .cover {        width: 160px;        height: 100px;    }    .mask{        width: 160px;        height: 100px;    }    .item{        width: 160px!important;    }    .img {        width: 160px;        height: 100px;        border-radius: 4px;    }}@media screen and (min-width: 1402px) {    .cover {        width: 190px!important;        height: 119px!important;    }    .mask {        width: 190px;        height: 119px;    }    .item{        width: 190px!important;    }    .img {        width: 190px;        height: 119px;        border-radius: 4px;    }}.cover {    cursor: pointer;    width: 160px;    height: 100px;    border-radius: 4px;}.item {    display: flex;    position: relative;    flex-direction: column;    font-size: 12px;    padding: 8px;    margin-bottom: 20px;}.mask {    position: absolute;    flex-direction: column;    border-radius: 4px;    display: flex;}.mask:hover .info {    opacity: 1;}.info {    display: flex;    flex-direction: column;    padding: 10px;    font-size: 14px;    justify-content: center;    height: 100%;    color: #e5e9ef;    cursor: pointer;    background-color: rgba(0, 0, 0, .45);    border-radius: 4px;    transition: all .3s;    opacity: 0;}.info span {    overflow: hidden;    text-overflow: ellipsis;    width: 100%;}.time {    color: #999999;}.name {    padding: 5px 0;    transition: color .3s;    cursor: pointer;}.name:hover {    color: #23aedb;}</style>