<template>    <div class="item">        <span class="index">{{index}}</span>        <div class="detail">            <div class="detail-left" @click="to(`/video/${item.dynamic.id}`)">                <img class="cover" :src="item.dynamic.coverSrc === null ? logo : item.dynamic.coverSrc" alt="">            </div>            <div class="detail-right">                <div class="video-title" @click="to(`/video/${item.dynamic.id}`)">{{item.dynamic.title}}</div>                <div class="video-user">                    <div class="user" @click="to(`/space/${item.user.id}`)">                        <img class="avatar" :src="logo" alt="">                        <span class="nickname">{{item.user.nickname}}</span>                    </div>                    <div class="option">                        <span v-if="item.isPlay">已观看</span> | <span class="delete" @click="deleteById(item)">删除</span>                    </div>                </div>            </div>        </div>    </div></template><script>import logo from '@/assets/logo.png'import request from '@/utils/request'import {DeleteLaterById} from '@/utils/Constant'import {useRouter} from 'vue-router'export default {    name: 'WatchLaterItem',    props: {        index: {            type: Number        },        item: {            type: Object        }    },    emits: ['updateLaterList'],    setup(props, ctx) {        let router = useRouter()        function deleteById(item) {            request.get(`${DeleteLaterById}/${item.later.id}`).then(res => {                if (res.data.code === 0) {                    ctx.emit('updateLaterList', item)                }            })        }        function to(path) {            window.open(router.resolve(path).href, '_blank')        }        return {            deleteById,            to,            logo,        }    },}</script><style scoped>.item {    display: flex;    align-items: center;    margin-top: 30px;}.index{    width: 80px;    font-size: 20px;    color: #222;    text-align: center;}.cover {    width: 160px;    height: 100px;    border-radius: 4px;    cursor: pointer;}.detail {    display: flex;    width: 100%;}.detail-left {    width: 160px;    margin-right: 20px;}.detail-right {    display: flex;    flex-direction: column;    width: 100%;    border-bottom: #e5e9ef solid 1px;}.video-title {    font-size: 14px;    color: #222;    font-weight: 700;    overflow: hidden;    display: inline-block;    text-overflow: ellipsis;    white-space: nowrap;    width: 740px;    transition: all .3s;    cursor: pointer;}.video-title:hover{    color: #23aedb;}.video-user{    color: #6d757a;    display: flex;    justify-content: space-between;    font-size: 12px;    margin-top: 20px;}.avatar{    width: 20px;    height: 20px;    border-radius: 50%;}.user {    display: flex;    align-items: center;    cursor: pointer;}.nickname{    margin-left: 10px;}.delete {    cursor: pointer;}</style>