<template>    <div class="background">        <Header :isSticky="true"/>        <div class="content">            <div class="dynamic" v-if="!loading">                <DynamicItem :item="item" :comment="true" :click="false"/>            </div>        </div>    </div></template><script>import Header from '@/components/Header'import DynamicItem from '@/components/DynamicItem'import {ref} from 'vue'import request from '@/utils/request'import {useRoute} from 'vue-router'import {GetDynamicById} from '@/utils/Constant'export default {    name: 'DynamicDetailPage',    components: {DynamicItem, Header},    setup() {        let item = ref({})        let route = useRoute()        let loading = ref(true)        request.post(GetDynamicById, {            dynamicId: route.params.id        }).then(res => {            if (res.data.code === 0) {                item.value = res.data.data            }        }).finally(() => {            loading.value = false        })        return {            item,            loading,        }    }}</script><style scoped>.content{    display: flex;    justify-content: center;    margin-top: 10px;}.background {    width: 100%;    min-height: 100vh;    background-color: #b3d5ff;}</style>