<template>    <div class="fans">        <div class="tabs">            <div class="kind">                <div class="title">                    我的关注                </div>                <div :class="path === 'follow' ? 'tab active' : 'tab'" @click="to('follow')">                    <span>全部关注</span>                    <span class="num">{{ subscribeNum }}</span>                </div>            </div>            <div class="kind">                <div class="title">                    我的粉丝                </div>                <div :class="path === 'fans' ? 'tab active' : 'tab'" @click="to('fans')">                    <span>我的粉丝</span>                    <span class="num">{{ fanNum }}</span>                </div>            </div>        </div>        <router-view/>    </div></template><script>import {onMounted, ref, watch} from 'vue'import request from '@/utils/request'import {GetFanNum, GetSubscribeNum} from '@/utils/Constant'import {useRoute, useRouter} from 'vue-router'export default {    name: 'Fan',    setup() {        let subscribeNum = ref(0)        let fanNum = ref(0)        let path = ref('follow')        let router = useRouter()        let route = useRoute()        function init() {            request.get(`${GetSubscribeNum}/${route.params.id}`).then(res => {                if (res.data.code === 0) {                    subscribeNum.value = res.data.data.subscribeNum                }            })            request.get(`${GetFanNum}/${route.params.id}`).then(res => {                if (res.data.code === 0) {                    fanNum.value = res.data.data.fanNum                }            })        }        onMounted(() => {            init()            let strings = route.path.split('/')            path.value = strings[strings.length - 1]        })        watch(() => route.path, (newVal) => {            let strings = newVal.split('/')            path.value = strings[strings.length - 1]        })        function to(newPath) {            path.value = newPath            router.push(`/space/${route.params.id}/fans/${newPath}`)        }        return {            fanNum,            subscribeNum,            path,            to,        }    },}</script><style scoped>@media screen and (max-width: 1402px) {    .fans {        width: 1101px;    }}@media screen and (min-width: 1402px) {    .fans {        width: 1283px;    }}.fans {    display: flex;    margin-top: 10px;    border: #eeeeee solid 1px;    background-color: white;    border-radius: 4px;}.tabs {    width: 159px;    color: #6d757a;    border-right: #eeeeee solid 1px;}.title {    padding-top: 20px;    padding-left: 20px;    margin-bottom: 10px;}.tab {    color: black;    display: flex;    justify-content: space-around;    align-items: center;    height: 44px;    cursor: pointer;}.num {    font-size: 14px;    color: #6d757a;}.active {    color: white;    background-color: #23aedb;}.active span {    color: white;}.kind {    border-bottom: #eeeeee solid 1px;}</style>