<template>    <div class="page">        <Header/>        <div class="item">            <div class="tabs">                <div class="icon">                    <svg>                        <path xmlns="http://www.w3.org/2000/svg"                              d="M15.6386295,4.17014918 C16.454219,4.39885202 15.6343208,5.07824066 15.6343208,5.07824066 L12.8711365,7.09359077 L4.27181047,2.64580413 C4.27181047,2.64580413 10.0809335,6.55511522 11.8533103,7.76666817 L5.86501964,11.7253113 C4.91770244,12.2710516 4.67094307,11.8350769 4.67094307,11.8350769 L4.25489009,10.97071 L0.0325132718,0.704758608 C-0.125784343,0.348173789 0.320384239,-0.0596727945 0.798575886,0.00727624935 L15.6386295,4.17014918 Z M10.2454612,9.59252937 C10.230576,9.58092963 10.2174075,9.56728156 10.2063473,9.55199119 C10.14161,9.46249359 10.1616822,9.3374615 10.2511798,9.27272421 L11.7512731,8.18764499 C11.7871922,8.16166319 11.8307257,8.14835156 11.875033,8.14980177 C11.9854308,8.15341518 12.0719967,8.24583954 12.0683833,8.35623737 L11.9964408,10.5542479 C11.995057,10.5965255 11.9803043,10.6372757 11.9543032,10.6706411 C11.8864076,10.757767 11.7607379,10.7733563 11.673612,10.7054607 L10.2454612,9.59252937 Z"                              id="纸飞机"                              transform="translate(8.000000, 6.000000) scale(-1, 1) translate(-8.000000, -6.000000) "/>                    </svg>                    <span>消息中心</span>                </div>                <div :class="tabIndex === 1 ? 'tab active' : 'tab'" @click="to('reply', 1)" style="margin-top: 40px">                    回复我的                </div><!--                <div :class="tabIndex === 2 ? 'tab active' : 'tab'" @click="tabIndex = 2">--><!--                    @ 我的--><!--                </div>-->                <div :class="tabIndex === 3 ? 'tab active' : 'tab'" @click="to('love', 3)">                    收到的赞                </div>                <div :class="tabIndex === 4 ? 'tab active' : 'tab'" @click="to('system', 4)">                    系统通知                </div>                <div :class="tabIndex === 5 ? 'tab active' : 'tab'" @click="to('whisper', 5)">                    我的消息                </div><!--                <div :class="tabIndex === 6 ? 'tab active' : 'tab'" @click="to('reply', 6)">--><!--                    私信存档--><!--                </div>-->            </div>            <div class="content">                <RouterView/>            </div>        </div>    </div></template><script>import Header from '@/components/Header'import {onMounted, ref} from 'vue'import {useRoute, useRouter} from 'vue-router'export default {    name: 'MessagePage',    components: {Header},    setup() {        let tabIndex = ref(1)        let router = useRouter()        let route = useRoute()        function to(path, index){            tabIndex.value = index            router.push(`/message/${path}`)        }        onMounted(() => {            let path = route.path            if (path.indexOf('reply') !== -1) {                tabIndex.value = 1            }            if (path.indexOf('love') !== -1) {                tabIndex.value = 3            }            if (path.indexOf('system') !== -1) {                tabIndex.value = 4            }            if (path.indexOf('whisper') !== -1) {                tabIndex.value = 5            }        })        return {            tabIndex,            to,        }    },}</script><style scoped>@media screen and (max-width: 1402px) {    .item {        width: 1101px;    }}@media screen and (min-width: 1402px) {    .item {        width: 1283px;    }}.page {    height: 100vh;    overflow: hidden;}.item {    margin: 0 auto;    display: flex;}.tabs {    width: 140px;    height: calc(100vh - 64px);    background-color: rgba(255, 255, 255, .8);}.content {    width: calc(100% - 140px);    padding: 10px;    height: calc(100vh - 84px);}.icon {    display: flex;    align-items: center;    justify-content: center;    margin: 20px 0;    font-size: 14px;    font-weight: bold;}.icon svg, path {    width: 20px;    height: 12px;}.tab {    display: flex;    align-content: center;    justify-content: center;    width: 100%;    position: relative;    font-size: 14px;    font-weight: bold;    cursor: pointer;    height: 40px;    line-height: 40px;}.tab:before {    content: '\25cf';    position: absolute;    left: 18px;    font-size: 14px;}.active {    color: #23aedb;}</style>