<template>    <div class="background">        <div class="comment-manager">            <div class="manager-header">                <div :class="tabIndex === 0 ? 'tab top-active' : 'tab'" @click="tabIndex = 0">视频评论</div>                <div :class="tabIndex === 1 ? 'tab top-active' : 'tab'" @click="tabIndex = 1">专栏评论</div>                <div class="bcc-input">                    <input placeholder="搜索评论" spellcheck="false" maxlength="999" minlength="0" type="text"                           class="input">                </div>            </div>            <div class="comment-manager-body">                <div class="title">全部评论</div>                <div class="option">                    <div class="option-left">                        <label class="checkbox">                            <span class="checkbox-checkbox">                                <input type="checkbox">                            </span>                            <span class="checkbox-label">全选</span>                        </label>                        <button class="disabled" disabled="disabled">                            <span>举报</span>                        </button>                        <button>                            <span>删除</span>                        </button>                    </div><!--                    <div class="option-right">--><!--                        <div class="operate-txt active">最近发布</div>--><!--                        <div class="operate-txt">点赞最多</div>--><!--                        <div class="operate-txt">回复最多</div>--><!--                    </div>-->                </div>            </div>        </div>    </div></template><script>import {ref} from 'vue'export default {    name: 'PlatformCommentManager',    setup() {        let tabIndex = ref(0)        return {            tabIndex,        }    },}</script><style scoped>.background {    width: 100%;    min-height: calc(100vh - 64px);    background-color: #f4f5f7;    overflow: auto;}.comment-manager {    margin: 0 auto;    width: 1040px;    padding: 20px;    background-color: white;    box-sizing: border-box;    max-height: calc(100vh - 64px);}.manager-header {    display: flex;    height: 64px;    position: relative;    border-bottom: 1px solid #e7e7e7;}.manager-header .tab {    margin-left: 30px;    height: 61px;    display: flex;    justify-content: center;    align-items: center;    cursor: pointer;}.top-active {    color: #00a1d6;    border-bottom: 3px solid #00a1d6;    font-weight: bolder;}.bcc-input {    width: 232px;    position: absolute;    right: 0;    top: 10px;}.input {    width: 100%;    padding: 0 12px;    -webkit-box-sizing: border-box;    box-sizing: border-box;    vertical-align: bottom;    font-size: 14px;    color: #212121;    border: 1px solid #e7e7e7;    border-radius: 4px;    background-color: transparent;    outline: none;    min-height: 32px;}.input:focus {    border: 1px solid #23aedb;}.title {    font-size: 20px;}.option {    display: flex;    justify-content: space-between;}.option-left{    display: flex;}.option-left button{    margin-left: 12px;    padding: 5px 16px;    border: 1px solid #e7e7e7;    background-color: #23aedb;    color: white;    cursor: pointer;}.option-left .disabled{    color: #212121;    opacity: .4;    background-color: #fff;    cursor: not-allowed;}.option-right {    display: flex;}.checkbox{    font-size: 14px;    color: #212121;    line-height: 18px;    margin-right: 24px;    cursor: pointer;    display: flex;    -webkit-box-align: center;    -ms-flex-align: center;    align-items: center;}.checkbox-checkbox{    width: 16px;    height: 16px;    position: relative;    display: block;    border-radius: 2px;}</style>