<template>    <div class="background">        <div class="platform-data">            <div class="tabs">                <div :class="tabIndex === 1 ? 'tab active' : 'tab'" @click="switchTab('/platform/dataCenter/video', 1)">视频</div>                <div :class="tabIndex === 2 ? 'tab active' : 'tab'" @click="switchTab('/platform/dataCenter/play', 2)">播放</div>                <div :class="tabIndex === 3 ? 'tab active' : 'tab'" @click="switchTab('/platform/dataCenter/audience', 3)">观众</div>                <div :class="tabIndex === 4 ? 'tab active' : 'tab'" @click="switchTab('/platform/dataCenter/read', 4)">专栏</div>            </div>            <div class="data-content">                <RouterView/>            </div>        </div>    </div></template><script>import {ref} from 'vue'import {useRouter} from 'vue-router'export default {    name: 'PlatformData',    setup() {        let tabIndex = ref(1)        let router = useRouter()        function switchTab(path, index) {            router.push(path)            tabIndex.value = index        }        return {            tabIndex,            switchTab,        }    }}</script><style scoped>.background{    background: #f6f7f8;    width: 100%;    min-height: calc(100vh - 64px);    max-height: calc(100vh - 64px);    overflow: auto;}.tabs {    display: flex;    width: 300px;    justify-content: space-around;}.tab {    font-size: 18px;    cursor: pointer;    position: relative;    height: 30px;}.tab:hover {    color: #23aedb;}.active:after {    content: "";    display: block;    position: absolute;    bottom: -8px;    left: 0;    right: 0;    width: 24px;    margin: auto;    background-color: #00aeec;    height: 4px;    border-radius: 9px;}.active {    color: #23aedb;}.platform-data{    margin: 0 auto;    width: 1040px;    padding: 20px 0;}.data-content{    margin-top: 20px;}</style>