<template>    <div class="background">        <div class="platform-data">            <div class="tabs">                <div :class="tabIndex === 1 ? 'tab active' : 'tab'" @click="tabIndex = 1">视频</div>                <div :class="tabIndex === 2 ? 'tab active' : 'tab'" @click="tabIndex = 2">播放</div>                <div :class="tabIndex === 3 ? 'tab active' : 'tab'" @click="tabIndex = 3">观众</div>                <div :class="tabIndex === 4 ? 'tab active' : 'tab'" @click="tabIndex = 4">专栏</div>            </div>            <div class="data-content">                <CoreData/>            </div>        </div>    </div></template><script>import {ref} from 'vue'import CoreData from '@/components/CoreData'export default {    name: 'PlatformData',    components: {CoreData},    setup() {        let tabIndex = ref(1)        return {            tabIndex,        }    }}</script><style scoped>.background{    background: #f6f7f8;    width: 100%;    min-height: calc(100vh - 64px);}.tabs {    display: flex;    width: 300px;    justify-content: space-around;}.tab {    font-size: 18px;    cursor: pointer;    position: relative;    height: 30px;}.tab:hover {    color: #23aedb;}.active:after {    content: "";    display: block;    position: absolute;    bottom: -8px;    left: 0;    right: 0;    width: 24px;    margin: auto;    background-color: #00aeec;    height: 4px;    border-radius: 9px;}.active {    color: #23aedb;}.platform-data{    margin: 0 auto;    width: 1040px;    padding: 20px 0;}.data-content{    margin-top: 20px;}</style>