<template>    <div class="tabs">        <div :class="tabIndex === 0 ? 'tab tab-active' : 'tab'" @click="tabIndex = 0">视频数据</div>        <div :class="tabIndex === 1 ? 'tab tab-active' : 'tab'" @click="tabIndex = 1">专栏数据</div>    </div>    <div class="tab-content">        <div class="video-content" v-if="tabIndex === 0">            <div class="info-card">                <div class="info-card-detail">                    <div class="title click">净增</div>                    <div class="num">{{ subscribeNum }}</div>                </div>            </div>            <div class="info-card">                <div class="info-card-detail">                    <div class="title click">播放量</div>                    <div class="num">{{ videoPlayNum }}</div>                </div>            </div>            <div class="info-card">                <div class="info-card-detail">                    <div class="title click">评论</div>                    <div class="num">{{ videoCommentNum }}</div>                </div>            </div>            <div class="info-card">                <div class="info-card-detail">                    <div class="title click">弹幕</div>                    <div class="num">{{ videoBarrageNum }}</div>                </div>            </div>            <div class="info-card">                <div class="info-card-detail">                    <div class="title click">点赞</div>                    <div class="num">{{ videoStarNum }}</div>                </div>            </div>            <div class="info-card">                <div class="info-card-detail">                    <div class="title click">收藏</div>                    <div class="num">{{ videoFavoritesNum }}</div>                </div>            </div>        </div>        <div class="text-content" v-if="tabIndex === 1">            <div class="text-info-card">                <div class="text-info-card-detail">                    <div class="title click">净增</div>                    <div class="num">{{ subscribeNum }}</div>                </div>            </div>            <div class="text-info-card">                <div class="text-info-card-detail">                    <div class="title click">阅读量</div>                    <div class="num">{{ textPlayNum }}</div>                </div>            </div>            <div class="text-info-card">                <div class="text-info-card-detail">                    <div class="title click">评论数</div>                    <div class="num">{{ textCommentNum }}</div>                </div>            </div>            <div class="text-info-card">                <div class="text-info-card-detail">                    <div class="title click">收藏数</div>                    <div class="num">{{ textFavoritesNum }}</div>                </div>            </div>            <div class="text-info-card">                <div class="text-info-card-detail">                    <div class="title click">点赞数</div>                    <div class="num">{{ textStarNum }}</div>                </div>            </div>        </div>    </div></template><script>import {useRouter} from 'vue-router'import {onMounted, ref} from 'vue'import request from '@/utils/request'import {    GetYesterdayBarrageData,    GetYesterdayCommentData, GetYesterdayFavoriteData,    GetYesterdayPlayData, GetYesterdayStarData,    GetYesterdaySubscribeData, READ_TYPE, VIDEO_TYPE,} from '@/utils/Constant'export default {    name: 'PlatformHome',    setup() {        let tabIndex = ref(0)        let router = useRouter()        let subscribeNum = ref(0)        let videoPlayNum = ref(0)        let videoCommentNum = ref(0)        let videoBarrageNum = ref(0)        let videoStarNum = ref(0)        let videoFavoritesNum = ref(0)        let textPlayNum = ref(0)        let textCommentNum = ref(0)        let textBarrageNum = ref(0)        let textStarNum = ref(0)        let textFavoritesNum = ref(0)        function init() {            request.get(GetYesterdaySubscribeData).then(res => {                if (res.data.code === 0) {                    subscribeNum.value = res.data.data.subscribeNum                }            })            request.get(`${GetYesterdayPlayData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    videoPlayNum.value = res.data.data.playNum                }            })            request.get(`${GetYesterdayCommentData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    videoCommentNum.value = res.data.data.commentNum                }            })            request.get(`${GetYesterdayBarrageData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    videoBarrageNum.value = res.data.data.barrageNum                }            })            request.get(`${GetYesterdayStarData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    videoStarNum.value = res.data.data.starNum                }            })            request.get(`${GetYesterdayFavoriteData}/${VIDEO_TYPE}`).then(res => {                if (res.data.code === 0) {                    videoFavoritesNum.value = res.data.data.collectNum                }            })            request.get(`${GetYesterdayPlayData}/${READ_TYPE}`).then(res => {                if (res.data.code === 0) {                    textPlayNum.value = res.data.data.playNum                }            })            request.get(`${GetYesterdayCommentData}/${READ_TYPE}`).then(res => {                if (res.data.code === 0) {                    textCommentNum.value = res.data.data.commentNum                }            })            request.get(`${GetYesterdayBarrageData}/${READ_TYPE}`).then(res => {                if (res.data.code === 0) {                    textBarrageNum.value = res.data.data.barrageNum                }            })            request.get(`${GetYesterdayStarData}/${READ_TYPE}`).then(res => {                if (res.data.code === 0) {                    textStarNum.value = res.data.data.starNum                }            })            request.get(`${GetYesterdayFavoriteData}/${READ_TYPE}`).then(res => {                if (res.data.code === 0) {                    textFavoritesNum.value = res.data.data.collectNum                }            })        }        onMounted(() => {            init()        })        return {            tabIndex,            subscribeNum,            videoPlayNum,            videoCommentNum,            videoBarrageNum,            videoStarNum,            videoFavoritesNum,            textPlayNum,            textCommentNum,            textBarrageNum,            textStarNum,            textFavoritesNum,        }    },}</script><style scoped>@media screen and (max-width: 1628px) {    .tab-content {        margin: 20px 72px;    }}@media screen and (min-width: 1628px) {    .tab-content {        width: 1283px;        margin: 0 auto;    }}.tabs {    display: flex;    align-items: center;}.tab {    height: 64px;    display: flex;    justify-content: center;    align-items: center;    cursor: pointer;    position: relative;    margin-left: 40px;}.tab-active {    color: #23aedb;    font-weight: 700;}.video-content{    display: flex;    flex-wrap: wrap;}.info-card {    box-sizing: border-box;    padding: 5px;    margin: 0;    width: 25%;    background: #fff;    border-radius: 2px;    height: 88px;    display: flex;    min-width: 195px;}.info-card-detail{    display: flex;    flex-direction: column;    justify-content: space-between;    padding: 16px;    background-color: rgba(0, 174, 236, .04);    height: calc(100% - 32px);    font-size: 14px;    opacity: .8;    width: calc(100% - 32px);    border-radius: 2px;}.num {    color: #00aeec;    font-weight: 800;    font-size: 22px;}.click {    cursor: pointer;}.text-content {    display: flex;    flex-wrap: wrap;}.text-info-card {    box-sizing: border-box;    padding: 5px;    margin: 0;    width: 33.33%;    background: #fff;    border-radius: 2px;    height: 88px;    display: flex;    min-width: 195px;}.text-info-card-detail {    display: flex;    flex-direction: column;    justify-content: space-between;    background-color: rgba(0, 174, 236, .04);    height: calc(100% - 32px);    font-size: 14px;    opacity: .8;    width: calc(100% - 32px);    border-radius: 2px;    padding: 16px 12.5%;}</style>