<template>    <Header :isSticky="true"/>    <div class="content">        <div class="left-nav">            <div :class="tabIndex === 0 ? 'nav active' : 'nav'" @click="to(`/platform/home`, 0)">首页</div>            <div :class="tabIndex === 1 ? 'nav active' : 'nav'" @click="to(`/platform/dataCenter`, 1)">数据中心</div>            <div :class="tabIndex === 2 ? 'nav active' : 'nav'" @click="to(`/platform/comment`, 2)">评论管理</div>            <div :class="tabIndex === 3 ? 'nav active' : 'nav'" @click="to(`/platform/barrage`, 3)">弹幕管理</div>        </div>        <div class="right">            <RouterView/>        </div>    </div></template><script>import Header from '@/components/Header'import {ref} from 'vue'import {useRouter} from 'vue-router'export default {    name: 'PlatformPage',    components: {Header},    setup() {        let tabIndex = ref(0)        let router = useRouter()        function switchTab(index, path) {            tabIndex.value = index            router.push(`/platform/${path}`)        }        function to(path, index) {            tabIndex.value = index            router.push(path)        }        return {            tabIndex,            switchTab,            to,        }    },}</script><style scoped>.content {    display: flex;    width: 100%;    height: calc(100vh - 64px);    overflow: auto;}.left-nav{    display: flex;    flex-direction: column;    width: 200px;    align-items: center;    max-height: calc(100vh - 64px);    overflow-y: auto;    box-sizing: border-box;    border-right: 1px solid #f4f4f4;    min-height: calc(100vh - 64px);}.nav {    min-height: 48px;    display: flex;    justify-content: center;    align-items: center;    width: 100%;    cursor: pointer;}.nav.active{    color: #23aedb;}.nav:hover{    background-color: #e8e8e8;}.right{    width: calc(100% - 200px);}</style>