<template>    <div class="title">数据概览</div>    <div class="info">        <div class="data-card">            <div :class="tabIndex === 0 ? 'data-detail card-active' : 'data-detail'" @click="tabIndex = 0">                <div class="name">阅读量</div>                <div class="value">{{ playNum }}</div>            </div>        </div>        <div class="data-card">            <div :class="tabIndex === 1 ? 'data-detail card-active' : 'data-detail'" @click="tabIndex = 1">                <div class="name">评论数</div>                <div class="value">{{ commentNum }}</div>            </div>        </div>        <div class="data-card">            <div :class="tabIndex === 2 ? 'data-detail card-active' : 'data-detail'" @click="tabIndex = 2">                <div class="name">点赞数</div>                <div class="value">{{ starNum }}</div>            </div>        </div>    </div>    <div>        <LastSevenDaysNum v-if="tabIndex === 0" :url="GetLastSevenDaysPlayData" :type="READ_TYPE" desc="近7日阅读数"/>        <LastSevenDaysNum v-if="tabIndex === 1" :url="GetLastSevenDaysCommentData" :type="READ_TYPE" desc="近7日评论数"/>        <LastSevenDaysNum v-if="tabIndex === 2" :url="GetLastSevenDaysStarData" :type="READ_TYPE" desc="近7日点赞数"/>    </div></template><script>import {onMounted, ref} from 'vue'import request from '@/utils/request'import {    GetLastSevenDaysCommentData,    GetLastSevenDaysPlayData,    GetLastSevenDaysStarData,    READ_TYPE,} from '@/utils/Constant'import LastSevenDaysNum from '@/components/LastSevenDaysNum'export default {    name: 'ReadData',    components: {LastSevenDaysNum},    setup() {        let tabIndex = ref(0)        let starNum = ref(0)        let playNum = ref(0)        let commentNum = ref(0)        function init() {            request.get(`${GetLastSevenDaysPlayData}/${READ_TYPE}`).then(res => {                if (res.data.code === 0) {                    for (let value of Object.values(res.data.data)) {                        playNum.value += parseInt(value.toString())                    }                }            })            request.get(`${GetLastSevenDaysCommentData}/${READ_TYPE}`).then(res => {                if (res.data.code === 0) {                    for (let value of Object.values(res.data.data)) {                        commentNum.value += parseInt(value.toString())                    }                }            })            request.get(`${GetLastSevenDaysStarData}/${READ_TYPE}`).then(res => {                if (res.data.code === 0) {                    for (let value of Object.values(res.data.data)) {                        starNum.value += parseInt(value.toString())                    }                }            })        }        onMounted(() => {            init()        })        return {            tabIndex,            starNum,            playNum,            commentNum,            READ_TYPE,            GetLastSevenDaysPlayData,            GetLastSevenDaysCommentData,            GetLastSevenDaysStarData,        }    },}</script><style scoped>.title {    margin-right: 4px;    color: #18191c;    font-weight: 600;    font-size: 18px;    line-height: 25px;}.info {    display: flex;    flex-wrap: wrap;    margin-top: 20px;}.data-card {    display: inline-block;    width: 195px;    height: 88px;    padding: 20px 6px;    vertical-align: middle;    position: relative;}.data-detail {    display: flex;    flex-direction: column;    justify-content: space-between;    width: 100%;    height: 100%;    box-sizing: border-box;    padding: 16px 16px 20px 16px;    color: #61666d;    cursor: pointer;    background-color: #fff;    border-radius: 16px;}.card-active {    background: #ff4684;    color: #fff;}.name {    font-size: 14px;    opacity: 0.8;}.value {    font-weight: 800;    font-size: 22px;    line-height: 22px;    box-sizing: border-box;    letter-spacing: 1px;}</style>