<template>    <div class="top">        <div class="title">收到的赞</div>    </div>    <div class="card" ref="card">        <StarItem v-for="item in list" :item="item"/>    </div></template><script>import {getCurrentInstance, onBeforeUnmount, onMounted, reactive, ref} from 'vue'import StarItem from '@/components/StarItem'import request from '@/utils/request'import {GetReply, GetStar} from '@/utils/Constant'export default {    name: 'ReceiveStar',    components: {StarItem},    setup() {        let isLoading = false        let isEnd = false        let pageSize = ref(20)        let card        let list = reactive([])        function init() {            request.post(GetStar, {                pageSize: pageSize.value,            }).then(res => {                if (res.data.code === 0) {                    list.push(...res.data.data)                }            })        }        onMounted(() => {            card = getCurrentInstance().refs.card            card.addEventListener('scroll', scrollLoad)            init()        })        onBeforeUnmount(() => {            card.removeEventListener('scroll', scrollLoad)        })        function scrollLoad() {            try {                if (!isLoading && !isEnd) {                    if (card.scrollHeight - card.scrollTop - card.clientHeight < 50) {                        let data = {                            pageSize: pageSize.value,                            commentId: list[list.length - 1].comment.id,                        }                        isLoading = true                        request.post(GetReply, data).then(res => {                            if (res.data.code === 0) {                                list.push(...res.data.data)                                if (res.data.data.length === 0) {                                    isEnd = true                                }                            }                        }).finally(() => {                            isLoading = false                        })                    }                }            } catch (e) {            }        }        return {            list,        }    },}</script><style scoped>.top {    width: calc(100% - 140px);    height: 42px;    background-color: white;    border-radius: 4px;    display: flex;    align-items: center;    box-shadow: 0 2px 4px 0 rgba(121, 146, 185, 0.54);}.title {    padding: 0 16px;    color: #666666;}.card {    margin-top: 10px;    width: calc(100% - 160px);    height: calc(100vh - 64px - 42px - 30px);    background-color: white;    border-radius: 4px;    display: flex;    align-items: center;    box-shadow: 0 2px 4px 0 rgba(121, 146, 185, 0.54);    min-height: 300px;    overflow: auto;    flex-direction: column;    padding: 0 10px;}</style>