<template>    <div class="background">        <Header/>        <div class="content">            <!--            <div class="left-nav">-->            <!--                <div class="nav">dsadsa</div>-->            <!--                <div class="nav">dsadsa</div>-->            <!--                <div class="nav">dsadsa</div>-->            <!--                <div class="nav">dsadsa</div>-->            <!--                <div class="nav">dsadsa</div>-->            <!--            </div>-->            <div class="right-content">                <div class="tabs">                    <div :class="tabIndex === 0 ? 'tab tab-active' : 'tab'" @click="switchTab(0, 'video')">视频投稿</div>                    <div :class="tabIndex === 1 ? 'tab tab-active' : 'tab'" @click="switchTab(1, 'text')">专栏投稿</div><!--                    <div :class="tabIndex === 2 ? 'tab tab-active' : 'tab'" @click="switchTab(2, 'video')">投稿</div>--><!--                    <div :class="tabIndex === 3 ? 'tab tab-active' : 'tab'" @click="switchTab(3, 'video')">投稿</div>--><!--                    <div :class="tabIndex === 4 ? 'tab tab-active' : 'tab'" @click="switchTab(4, 'video')">投稿</div>--><!--                    <div :class="tabIndex === 5 ? 'tab tab-active' : 'tab'" @click="switchTab(5, 'video')">投稿</div>-->                </div>                <RouterView/>            </div>        </div>    </div></template><script>import Header from '@/components/Header'import {ref} from 'vue'import {useRouter} from 'vue-router'export default {    name: 'UploadPage',    components: {Header},    setup() {        let tabIndex = ref(0)        let router = useRouter()        function switchTab(index, path) {            tabIndex.value = index            router.push(`/upload/${path}`)        }        return {            tabIndex,            switchTab,        }    },}</script><style scoped>.left-nav {    max-width: 200px;    display: flex;    flex-direction: column;    align-items: center;    position: fixed;}.background {    width: 100%;    min-height: 100vh;}.content {    display: flex;    width: 100%;    height: 100%;}.nav {    display: flex;    justify-content: center;    align-items: center;    width: 144px;    height: 46px;}.right-content {    display: flex;    flex-direction: column;    width: 100%;}.tabs {    display: flex;    justify-content: center;    align-items: center;}.tab {    height: 64px;    display: flex;    justify-content: center;    align-items: center;    cursor: pointer;    position: relative;    margin-left: 40px;}.tab-active {    color: #23aedb;}.tab-active:after {    position: absolute;    bottom: 0;    left: 0;    right: 0;    content: "";    -webkit-transform: scaleY(.5);    transform: scaleY(.5);    background-color: #23aedb;    height: 5px;}</style>