<template>    <div class="input">        <div class="sign">标题</div>        <div class="input-container">            <div class="input-instance">                <input class="input-val" type="text" v-model="dynamic.title">            </div>        </div>    </div>    <div class="edit">        <div id="editor">            <textarea></textarea>        </div>    </div>    <div class="button" @click="upload">投稿</div></template><script>import {onMounted, ref} from 'vue'import {BASEURL, SubmitDynamic, UploadImageURL} from '@/utils/Constant'import request from '@/utils/request'export default {    name: 'UploadText',    setup() {        let dynamic = ref({            title: '',            content: '',            type: 2,        })        let editor        onMounted(() => {            editor = editormd('editor', {                placeholder: '请输入内容',                height: 700,                path: '/static/editor-md/lib/',                imageUpload: true,                imageFormats: ['jpg', 'jpeg', 'png', 'gif'],                imageUploadURL: `${BASEURL}${UploadImageURL}`,            })        })        function upload(){            console.log(editor.getMarkdown())            dynamic.value.content = editor.getMarkdown()            request.post(SubmitDynamic, dynamic.value).then(res => {                if (res.data.code === 0) {                }            })        }        return {            dynamic,            upload,        }    },}</script><style scoped>.edit {    width: calc(100% - 40px);    display: flex;    justify-content: flex-start;    align-items: flex-start;    margin: 0 auto;    flex-direction: column;    max-height: 100%;}.button {    margin-top: 10px;    width: 200px;    height: 44px;    cursor: pointer;    background: #00a1d6;    border-radius: 4px;    display: flex;    justify-content: center;    align-items: center;    color: white;    font-size: 14px;    margin-left: 20px;}.input-container {    background: hsla(0, 0%, 84.7%, 0);    border: 1px solid #ccd0d7;    border-radius: 4px;    padding: 0 12px;    display: flex;    align-items: center;    flex-wrap: wrap;    transition: border .3s ease;    flex: 7;}.input-instance {    min-width: 200px;    margin: 6px 0;    width: 100%;}.input-val {    display: block;    width: 100%;    color: #222;    line-height: 22px;    height: 22px;    font-size: 14px;    outline: 0;    -webkit-appearance: none;    -moz-appearance: none;    appearance: none;    border: 0;    background-color: transparent;}.input {    display: flex;    margin: 20px;}.sign {    flex: 1;    display: flex;    justify-content: center;    align-items: center;}</style>