<template>    <div class="item">        <div class="content">            <div class="tabs" ref="tabs">                <div :class="tabIndex === 1 ? 'tab active' : 'tab'">                    <span>视频</span>                    <span>{{ videoNum }}</span>                </div>            </div>            <div class="video">                <div class="video-item">                    <VideoItem v-for="item in videoList" :item="item" :key="item.dynamic.id"/>                </div>                <Pagination :total="Math.ceil(videoNum / pageSize)" :pageNum="pageNum" @update="updatePage"/>            </div>        </div>    </div></template><script>import {useRoute} from 'vue-router'import {onMounted, reactive, ref} from 'vue'import request from '@/utils/request'import {GetVideoDynamic, GetVideoDynamicNum} from '@/utils/Constant'import Pagination from '@/components/Pagination'import VideoItem from '@/components/VideoItem'export default {    name: 'UserVideo',    components: {VideoItem, Pagination},    setup() {        let route = useRoute()        let videoNum = ref(0)        let pageNum = ref(1)        let pageSize = ref(20)        let tabIndex = ref(1)        let videoList = reactive([])        onMounted(() => {            request.get(`${GetVideoDynamicNum}/${route.params.id}`).then(res => {                if (res.data.code === 0) {                    videoNum.value = res.data.data.dynamicNum                }            })            init()        })        function init(){            request.post(GetVideoDynamic, {                userId: route.params.id,                pageSize: pageSize.value,                pageNum: pageNum.value            }).then(res => {                if (res.data.code === 0) {                    console.log(res.data.data)                    videoList.length = 0                    videoList.push(...res.data.data)                }            })        }        function updatePage(value) {            pageNum.value = value            init()        }        return {            videoNum,            pageNum,            pageSize,            tabIndex,            videoList,            updatePage,        }    },}</script><style scoped>@media screen and (max-width: 1402px) {    .item {        width: 1082px;    }}@media screen and (min-width: 1402px) {    .item {        width: 1262px;    }}.item {    width: 100%;}.content {    margin-top: 10px;    display: flex;    border: #eeeeee solid 1px;    background-color: white;    border-radius: 4px;    min-height: 518px;}.tabs {    width: 179px;    border-right: #eeeeee solid 1px;    padding-top: 10px;}.tab {    height: 44px;    padding: 0 30px;    display: flex;    justify-content: space-between;    align-items: center;    cursor: pointer;    font-size: 14px;}.active {    color: white;    background-color: #23aedb;}.video-item{    display: flex;    flex-wrap: wrap;    justify-content: flex-start;    padding: 10px;}.video{    width: calc(100% - 179px);    display: flex;    flex-direction: column;}</style>